package net.zdsoft.eis.base.converter.asynchronous;import java.util.concurrent.Callable;/** * 异步任务执行 * @author shenl * */public abstract class EisCallable implements Callable {	protected String name;	private Object data;		public EisCallable(){		this("");	}		public EisCallable(String name){		this.name = name;	}		@Override	public Object call() throws Exception {		return run();	}		public abstract Object run() throws Exception;	public void setData(Object data) {		this.data = data;	}	public Object getData() {		return data;	}}