package net.zdsoft.eis.base.converter.dao;import java.util.Date;import java.util.List;import net.zdsoft.eis.base.converter.entity.ConverterFile;import net.zdsoft.keel.util.Pagination;public interface ConverterFileDao {	/**	 * 更新正在转换的记录为待执行	 */	public Integer updateJobInHand2NoHand(int resetTime);	/**	 * 更新任务完成	 * 	 * @param job	 */	public Integer updateJobFinished(ConverterFile job);	/**	 * 更新任务开始运行时间	 * 	 * @param jobRunTime	 * @param id	 * 	 * @return	 */	public Integer updateRunTime(int status, Date jobRunTime, String id);	/**	 * 获取接下来要进行转换的n条记录数	 * 	 * @param extNames	 *            支持的文件扩展名	 * @param status	 *            转换状态	 * @param n	 *            取多少条记录	 * @return	 */	public List<ConverterFile> findNextNoHandJobs(Pagination page, String[] extNames,			Integer status);}