package net.zdsoft.eis.base.converter.entity;import net.zdsoft.eis.base.converter.util.ResourceUtils;import net.zdsoft.leadin.common.entity.BusinessTask;public class ConverterFile extends BusinessTask {	private static final long serialVersionUID = 3496462747824130045L;	private String resourceType;	private String dirId;	private ConverterProperties properties;	public String getResourceType() {		return resourceType;	}	public void setResourceType(String resourceType) {		this.resourceType = resourceType;	}	public ConverterProperties getProperties() {		return properties;	}	public void setProperties(ConverterProperties properties) {		this.properties = properties;	}	public String getDirId() {		return dirId;	}	public void setDirId(String dirId) {		this.dirId = dirId;	}		/**	 * 获取转换状态	 * 	 * @return	 */	public String getStatusTitle() {		return ResourceUtils.getStatusTitle(getStatus());	}}