package net.zdsoft.eisu.base.common.service.impl;import java.util.List;import java.util.Map;import net.zdsoft.eisu.base.common.dao.TeachBuildingDao;import net.zdsoft.eisu.base.common.entity.TeachBuilding;import net.zdsoft.eisu.base.common.service.TeachBuildingService;/** * 楼层信息表  * @author  *  */public class TeachBuildingServiceImpl implements TeachBuildingService{	private TeachBuildingDao teachBuildingDao;		public void setTeachBuildingDao(TeachBuildingDao teachBuildingDao) {		this.teachBuildingDao = teachBuildingDao;	}	/**	 * 根据id获取楼层信息表	 * @param id	 * @return	 */	@Override	public TeachBuilding getBaseTeachBuildingById(String id){		return teachBuildingDao.getBaseTeachBuildingById(id);	}		/**	 * 根据ids数组查询楼层信息表map	 * @param ids	 * @return	 */	@Override	public Map<String, TeachBuilding> getBaseTeachBuildingMapByIds(String[] ids){		return teachBuildingDao.getBaseTeachBuildingMapByIds(ids);	}		@Override	public List<TeachBuilding> getBaseTeachBuildingByBuildingType(			String unitId, String areaId, String buildingType) {		return teachBuildingDao.getBaseTeachBuildingByBuildingType(unitId, areaId, buildingType);	}}