package net.zdsoft.eis.base.common.service.impl;import net.zdsoft.eis.base.common.dao.SchoolBuildingAreaDao;import net.zdsoft.eis.base.common.entity.SchoolBuildingArea;import net.zdsoft.eis.base.common.service.SchoolBuildingAreaService;/** * 校舍面积  * @author  *  */public class SchoolBuildingAreaServiceImpl implements SchoolBuildingAreaService{	private SchoolBuildingAreaDao schoolBuildingAreaDao;		/**	 * 新增校舍面积	 * @param schoolBuildingArea	 * @return	 */	@Override	public SchoolBuildingArea save(SchoolBuildingArea schoolBuildingArea){		return schoolBuildingAreaDao.save(schoolBuildingArea);	}		/**	 * 根据ids数组删除校舍面积数据	 * @param ids	 * @return	 */	@Override	public Integer delete(String[] ids){		return schoolBuildingAreaDao.delete(ids);	}		/**	 * 更新校舍面积	 * @param schoolBuildingArea	 * @return	 */	@Override	public Integer update(SchoolBuildingArea schoolBuildingArea){		return schoolBuildingAreaDao.update(schoolBuildingArea);	}		/**	 * 根据id获取校舍面积	 * @param id	 * @return	 */	@Override	public SchoolBuildingArea getSchoolBuildingAreaById(String id){		return schoolBuildingAreaDao.getSchoolBuildingAreaById(id);	}		public void setSchoolBuildingAreaDao(SchoolBuildingAreaDao schoolBuildingAreaDao){		this.schoolBuildingAreaDao = schoolBuildingAreaDao;	}}