package net.zdsoft.eis.base.common.service.impl;import net.zdsoft.eis.base.common.dao.SchoolBuildingDao;import net.zdsoft.eis.base.common.entity.SchoolBuilding;import net.zdsoft.eis.base.common.service.SchoolBuildingService;/** * 校舍  * @author  *  */public class SchoolBuildingServiceImpl implements SchoolBuildingService{	private SchoolBuildingDao schoolBuildingDao;		/**	 * 新增校舍	 * @param schoolBuilding	 * @return	 */	@Override	public SchoolBuilding save(SchoolBuilding schoolBuilding){		return schoolBuildingDao.save(schoolBuilding);	}		/**	 * 根据ids数组删除校舍数据	 * @param ids	 * @return	 */	@Override	public Integer delete(String[] ids){		return schoolBuildingDao.delete(ids);	}		/**	 * 更新校舍	 * @param schoolBuilding	 * @return	 */	@Override	public Integer update(SchoolBuilding schoolBuilding){		return schoolBuildingDao.update(schoolBuilding);	}		/**	 * 根据id获取校舍	 * @param id	 * @return	 */	@Override	public SchoolBuilding getSchoolBuildingById(String id){		return schoolBuildingDao.getSchoolBuildingById(id);	}		public void setSchoolBuildingDao(SchoolBuildingDao schoolBuildingDao){		this.schoolBuildingDao = schoolBuildingDao;	}}